// Helper function to format date
const formatDate = (dateString?: string): string => {
  if (!dateString) return '';

  const date = new Date(dateString);

  // Format as full date and time
  return date.toLocaleDateString('pt-BR', { 
    year: 'numeric', 
    month: 'long', 
    day: 'numeric',
    hour: '2-digit',
    minute: '2-digit'
  });
};

