const formatDate = (dateString?: string): string => {
  if (!dateString) return '';

  const date = new Date(dateString);

  // Format as dd/MM/yyyy HH:mm
  const day = date.getDate().toString().padStart(2, '0');
  const month = (date.getMonth() + 1).toString().padStart(2, '0');
  const year = date.getFullYear();
  const hours = date.getHours().toString().padStart(2, '0');
  const minutes = date.getMinutes().toString().padStart(2, '0');

  return `${day}/${month}/${year} ${hours}:${minutes}`;
};

interface SentEmail {
  id: string;
  to: string;
  cc?: string;
  bcc?: string;
  subject: string;
  content: string;
  date: string;
  from: string;
}

interface EmailListProps {
  folder: string;
  searchQuery: string;
  selectedEmail: string | null;
  onEmailSelect: (emailId: string) => void;
  sentEmails?: SentEmail[];
  onUnreadCountChange?: (count: number) => void;
}

function EmailList({
  folder,
  searchQuery,
  selectedEmail,
  onEmailSelect,
  sentEmails = [],
  onUnreadCountChange
}: EmailListProps) {
  const [selectedEmails, setSelectedEmails] = useState<string[]>([]);
  const [syncLoading, setSyncLoading] = useState(false);
  const {toast} = useToast();

  const {data, error, loading, refetch} = useEmails({});


  const syncAndFetchEmails = async () => {
    try {
      setSyncLoading(true);

      await apiClient.sincronizarEmails();
      await refetch();

      toast({
        title: "Emails sincronizados",
        description: "Seus emails foram sincronizados com sucesso.",
      });
    } catch (err) {
      console.error('Error syncing emails:', err);
      toast({
        title: "Erro ao sincronizar emails",
        description: "Não foi possível sincronizar seus emails. Tente novamente mais tarde.",
        variant: "destructive",
      });
    } finally {
      setSyncLoading(false);
    }
  };

  useEffect(() => {
    if (error) {
      toast({
        title: "Erro ao carregar emails",
        description: "Não foi possível carregar os emails. Tente novamente mais tarde.",
        variant: "destructive",
      });
    }
  }, [error, toast]);

  const apiEmails: Email[] = (() => {
    if (!data?.items) return [];
    return data.items.map((apiEmail: ApiEmail) => ({
      id: apiEmail.id_email.toString(),
      from: apiEmail.remetente,
      subject: apiEmail.assunto,
      preview: apiEmail.conteudo,
      date: formatDate(apiEmail.prazo_resposta),
      isRead: apiEmail.tp_status !== 'NOVO',
      hasAttachment: false,
      labels: [],
    }));
  })();

  // Simplify emails computation - remove complex useMemo chain
  const emails: Email[] = (() => {
    if (folder === 'sent') {
      return sentEmails.map(email => ({
        id: email.id,
        from: 'Você',
        subject: email.subject,
        preview: email.content.replace(/<[^>]*>/g, ''),
        date: formatDate(email.date),
        isRead: true,
        hasAttachment: false,
        labels: [],
      }));
    }

    const formattedMockEmails = MOCK_EMAILS.map(email => ({
      ...email,
      date: formatDate(email.date)
    }));

    if (folder === 'inbox') {
      return [...formattedMockEmails, ...apiEmails];
    }
    return apiEmails;
  })();


  useEffect(() => {
    if (onUnreadCountChange && folder === 'inbox') {
      const allEmails = [...MOCK_EMAILS, ...apiEmails];
      const unreadCount = allEmails.filter(email => !email.isRead).length;
      const safeCount = Math.max(0, unreadCount);
      onUnreadCountChange(safeCount);
    }
  }, [apiEmails, onUnreadCountChange, folder]);


  // Simplify filtered emails - remove heavy useMemo
  const filteredEmails = (() => {
    if (!searchQuery) {
      return emails;
    }

    const query = searchQuery.toLowerCase();
    return emails.filter(email =>
      email?.subject?.toLowerCase().includes(query) ||
      email?.from?.toLowerCase().includes(query) ||
      email?.preview?.toLowerCase().includes(query)
    );
  })();

  // Remove useCallback from toggleEmailSelection
  const toggleEmailSelection = (emailId: string) => {
    setSelectedEmails(prev =>
      prev.includes(emailId)
        ? prev.filter(id => id !== emailId)
        : [...prev, emailId]
    );
  };

  // Remove useCallback from selectAllEmails
  const selectAllEmails = () => {
    setSelectedEmails(
      selectedEmails.length === filteredEmails.length
        ? []
        : filteredEmails.map(email => email.id)
    );
  };

  return (
    <div className="flex-1 flex flex-col">
      {/* Email Actions */}
      <div className="p-4 border-b border-gray-200">
        <div className="flex items-center justify-between min-h-[2rem]">
          <div className="flex items-center space-x-2">
            <Checkbox
              checked={selectedEmails.length === filteredEmails.length && filteredEmails.length > 0}
              onCheckedChange={selectAllEmails}
              disabled={loading || filteredEmails.length === 0}
            />
            <span className="text-sm text-gray-600 whitespace-nowrap">
              {loading ? (
                "Carregando..."
              ) : selectedEmails.length > 0 ? (
                `${selectedEmails.length} selecionados`
              ) : (
                `${filteredEmails.length} emails`
              )}
            </span>
          </div>

          <div className="flex items-center space-x-1">
            {selectedEmails.length > 0 ? (
              <Button variant="ghost" size="sm">
                <Trash2 className="h-4 w-4"/>
              </Button>
            ) : (
              <Button
                variant="ghost"
                size="sm"
                onClick={() => syncAndFetchEmails()}
                disabled={loading || syncLoading}
              >
                {loading || syncLoading ? (
                  <Loader2 className="h-4 w-4 animate-spin"/>
                ) : (
                  <RotateCw className="h-4 w-4"/>
                )}
              </Button>
            )}
          </div>
        </div>
      </div>

      {/* Email List */}
      <div className="flex flex-col-reverse overflow-y-auto">
        {loading ? (
          <div className="flex flex-col items-center justify-center h-full p-8">
            <Loader2 className="h-8 w-8 text-blue-500 animate-spin mb-4"/>
            <p className="text-gray-500">Carregando emails...</p>
          </div>
        ) : filteredEmails.length === 0 ? (
          <div className="flex flex-col items-center justify-center h-full p-8">
            <Mail className="h-12 w-12 text-gray-300 mb-4"/>
            <h3 className="text-lg font-medium text-gray-900 mb-1">Nenhum email encontrado</h3>
            <p className="text-gray-500 text-center">
              {searchQuery ?
                "Não encontramos emails correspondentes à sua pesquisa." :
                "Não há emails nesta pasta no momento."}
            </p>
          </div>
        ) : (
          filteredEmails.map((email) => (
            <div
              key={email.id}
              className={cn(
                "p-4 border-b border-gray-100 cursor-pointer hover:bg-gray-50 transition-colors",
                selectedEmail === email.id && "bg-blue-100",
                !email.isRead && "bg-blue-50/30"
              )}
              onClick={() => {
                if (selectedEmail === email.id) {
                  onEmailSelect('');
                } else {
                  onEmailSelect(email.id);
                }
              }}
            >
              <div className="flex items-start space-x-3">
                <Checkbox
                  checked={selectedEmails.includes(email.id)}
                  onCheckedChange={() => toggleEmailSelection(email.id)}
                  onClick={(e) => e.stopPropagation()}
                />
                <div className="flex-1 min-w-0">
                  <div className="flex items-center justify-between mb-1">
                    <div className="flex items-center space-x-2">
                      <span className={cn(
                        "text-sm truncate",
                        "font-semibold text-gray-700"
                      )}>
                        {email.from}
                      </span>
                      {email.hasAttachment && (
                        <Paperclip className="h-3 w-3 text-gray-400"/>
                      )}
                    </div>
                    <span className="text-xs text-gray-500">{email.date}</span>
                  </div>

                  <h3 className={cn(
                    "text-sm mb-1 truncate",
                    "font-bold text-gray-900"
                  )}>
                    {email.subject}
                  </h3>

                  <p className="text-xs text-gray-500 line-clamp-2">
                    {email.preview}
                  </p>
                </div>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
}

export default EmailList;

'use client';

import { useState } from 'react';
import { 
  Table, 
  TableBody, 
  TableCell, 
  TableHead, 
  TableHeader, 
  TableRow 
} from '@/components/ui/table';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Badge } from '@/components/ui/badge';
import {
  Plus, 
  Search, 
  Edit, 
  Trash2, 
  Filter, 
  ArrowUpDown,
  MoreHorizontal
} from 'lucide-react';
import { Tema } from '@/lib/types';
import {mockTemas, mockAreas, getAreaNameById} from '@/lib/mockData';
import { TemaModal } from './TemaModal';
import {DropdownMenu, DropdownMenuContent, DropdownMenuItem, DropdownMenuTrigger} from "@/components/ui/dropdown-menu";

export default function TemasTable() {
  const [temas, setTemas] = useState<Tema[]>(mockTemas);
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedTema, setSelectedTema] = useState<Tema | null>(null);
  const [showTemaModal, setShowTemaModal] = useState(false);
  const [sortField, setSortField] = useState<keyof Tema | null>(null);
  const [sortDirection, setSortDirection] = useState<'asc' | 'desc'>('asc');

  const handleSort = (field: keyof Tema) => {
    if (sortField === field) {
      setSortDirection(sortDirection === 'asc' ? 'desc' : 'asc');
    } else {
      setSortField(field);
      setSortDirection('asc');
    }
  };

  const sortedTemas = [...temas].sort((a, b) => {
    if (!sortField) return 0;

    const aValue = a[sortField];
    const bValue = b[sortField];

    if (aValue === bValue) return 0;

    const direction = sortDirection === 'asc' ? 1 : -1;

    if (typeof aValue === 'string' && typeof bValue === 'string') {
      return aValue.localeCompare(bValue) * direction;
    }

    if (aValue < bValue) return -1 * direction;
    return 1 * direction;
  });

  const filteredTemas = sortedTemas.filter(tema => 
    tema.dsTema.toLowerCase().includes(searchQuery.toLowerCase()) ||
    tema.nmTema.toLowerCase().includes(searchQuery.toLowerCase()) ||
    tema.idAreas.some(areaId => getAreaNameById(areaId).toLowerCase().includes(searchQuery.toLowerCase()))
  );

  const handleCreateTema = () => {
    setSelectedTema(null);
    setShowTemaModal(true);
  };

  const handleEditTema = (tema: Tema) => {
    setSelectedTema(tema);
    setShowTemaModal(true);
  };

  const handleDeleteTema = (id: string) => {
    if (window.confirm('Tem certeza que deseja excluir este tema?')) {
      setTemas(temas.filter(tema => tema.idTema !== id));
    }
  };

  const handleSaveTema = (tema: Tema) => {
    if (selectedTema) {
      // Update existing tema
      setTemas(temas.map(t => t.idTema === tema.idTema ? tema : t));
    } else {
      // Add new tema
      setTemas([...temas, tema]);
    }
    setShowTemaModal(false);
    setSelectedTema(null);
  };

  const getAreasForTema = (idAreas: string[]) => {
    return mockAreas.filter(area => idAreas.includes(area.idArea));
  };

  return (
    <div className="h-full flex flex-col bg-gray-50">
      {/* Header */}
      <div className="bg-white border-b border-gray-200 p-6">
        <div className="flex items-center justify-between mb-4">
          <h1 className="text-2xl font-bold text-gray-900">
            Cadastro de Temas
          </h1>
          <Button 
            onClick={handleCreateTema} 
            className="bg-blue-600 hover:bg-blue-700"
          >
            <Plus className="h-4 w-4 mr-2" />
            Novo Tema
          </Button>
        </div>

        <div className="flex items-center space-x-4">
          <div className="flex-1 relative">
            <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-gray-400" />
            <Input
              placeholder="Pesquisar por nome, número ou área..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="pl-10 h-10 bg-gray-50 border-gray-200 focus:bg-white"
            />
          </div>
          <Button variant="secondary" className="h-10 px-4">
            <Filter className="h-4 w-4 mr-2" />
            Filtrar
          </Button>
        </div>
      </div>

      {/* Table */}
      <div className="flex-1 overflow-auto">
        <Table>
          <TableHeader className="bg-gray-50">
            <TableRow>
              <TableHead className="w-16 cursor-pointer" onClick={() => handleSort('nmTema')}>
                <div className="flex items-center">
                  Área
                  <ArrowUpDown className="ml-2 h-4 w-4" />
                </div>
              </TableHead>
              <TableHead className="cursor-pointer" onClick={() => handleSort('dsTema')}>
                <div className="flex items-center">
                  Nome
                  <ArrowUpDown className="ml-2 h-4 w-4" />
                </div>
              </TableHead>
              <TableHead className="cursor-pointer" onClick={() => handleSort('idAreas')}>
                <div className="flex items-center">
                  Áreas Relacionadas
                  <ArrowUpDown className="ml-2 h-4 w-4" />
                </div>
              </TableHead>
              <TableHead className="cursor-pointer" onClick={() => handleSort('nrDiasPrazo')}>
                <div className="flex items-center">
                  Prazo (dias)
                  <ArrowUpDown className="ml-2 h-4 w-4" />
                </div>
              </TableHead>
              <TableHead className="cursor-pointer" onClick={() => handleSort('tpContagem')}>
                <div className="flex items-center">
                  Tipo de Contagem
                  <ArrowUpDown className="ml-2 h-4 w-4" />
                </div>
              </TableHead>
              <TableHead className="cursor-pointer" onClick={() => handleSort('dtCadastro')}>
                <div className="flex items-center">
                  Data de Cadastro
                  <ArrowUpDown className="ml-2 h-4 w-4" />
                </div>
              </TableHead>
              <TableHead className="w-24 text-right">Ações</TableHead>
            </TableRow>
          </TableHeader>
          <TableBody>
            {filteredTemas.length === 0 ? (
              <TableRow>
                <TableCell colSpan={7} className="text-center py-8 text-gray-500">
                  Nenhum tema encontrado
                </TableCell>
              </TableRow>
            ) : (
              filteredTemas.map((tema) => {
                const areas = getAreasForTema(tema.idAreas);
                return (
                  <TableRow key={tema.idTema} className="hover:bg-gray-50">
                    <TableCell className="font-medium">{tema.nmTema}</TableCell>
                    <TableCell>{tema.dsTema}</TableCell>
                    <TableCell>
                      <div className="flex flex-wrap gap-1 max-w-sm">
                        {areas.length === 0 ? (
                          <span className="text-muted-foreground text-sm">Nenhuma área</span>
                        ) : (
                          areas.map((area) => (
                            <Badge key={area.idArea} variant="outline" className="text-xs">
                              {area.nmArea}
                            </Badge>
                          ))
                        )}
                      </div>
                    </TableCell>
                    <TableCell>{tema.nrDiasPrazo}</TableCell>
                    <TableCell>{tema.tpContagem}</TableCell>
                    <TableCell>{new Date(tema.dtCadastro).toLocaleDateString('pt-BR')}</TableCell>
                    <TableCell className="text-right">
                      <div className="flex justify-end space-x-2">
                        <Button
                          variant="ghost"
                          size="icon"
                          onClick={() => handleEditTema(tema)}
                        >
                          <Edit className="h-4 w-4" />
                        </Button>
                        <Button
                          variant="ghost"
                          size="icon"
                          onClick={() => handleDeleteTema(tema.idTema)}
                          className="text-red-500 hover:text-red-700"
                        >
                          <Trash2 className="h-4 w-4" />
                        </Button>
                        <DropdownMenu>
                          <DropdownMenuTrigger asChild>
                            <Button variant="ghost" className="h-8 w-8 p-0">
                              <MoreHorizontal className="h-4 w-4" />
                              <span className="sr-only">Abrir menu</span>
                            </Button>
                          </DropdownMenuTrigger>
                          <DropdownMenuContent align="end">
                            <DropdownMenuItem onClick={() => handleEditTema(tema)}>
                              <Edit className="mr-2 h-4 w-4" />
                              Editar
                            </DropdownMenuItem>
                            <DropdownMenuItem
                              onClick={() => handleDeleteTema(tema.idTema)}
                              className="text-destructive"
                            >
                              <Trash2 className="mr-2 h-4 w-4" />
                              Excluir
                            </DropdownMenuItem>
                          </DropdownMenuContent>
                        </DropdownMenu>
                      </div>
                    </TableCell>
                  </TableRow>
                );
              })
            )}
          </TableBody>
        </Table>
      </div>

      {/* Tema Modal */}
      {showTemaModal && (
        <TemaModal
          tema={selectedTema}
          isOpen={selectedTema !== null}
          onClose={() => {
            setShowTemaModal(false);
            setSelectedTema(null);
          }}
          onSave={handleSaveTema}
        />
      )}
    </div>
  );
}

'use client'

import { useState } from 'react'
import { Button } from '@/components/ui/button'
import { Input } from '@/components/ui/input'
import { Badge } from '@/components/ui/badge'
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from '@/components/ui/table'
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuTrigger,
} from '@/components/ui/dropdown-menu'
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card'
import { Search, Plus, MoreHorizontal, Edit, Trash2 } from 'lucide-react'
import { Tema } from '@/lib/types'
import { mockTemas, mockAreas } from '@/lib/mockData'
import { TemaModal } from './TemaModal'

interface TemasTableProps {
  onEdit?: (tema: Tema) => void
  onDelete?: (temaId: string) => void
}

export function TemasTable({ onEdit, onDelete }: TemasTableProps) {
  const [temas, setTemas] = useState<Tema[]>(mockTemas)
  const [searchTerm, setSearchTerm] = useState('')
  const [isModalOpen, setIsModalOpen] = useState(false)
  const [editingTema, setEditingTema] = useState<Tema | null>(null)

  const filteredTemas = temas.filter(tema =>
    tema.nmTema.toLowerCase().includes(searchTerm.toLowerCase()) ||
    tema.dsTema.toLowerCase().includes(searchTerm.toLowerCase())
  )

  const getAreasForTema = (idAreas: string[]) => {
    return mockAreas.filter(area => idAreas.includes(area.idArea))
  }

  const handleEdit = (tema: Tema) => {
    setEditingTema(tema)
    setIsModalOpen(true)
    onEdit?.(tema)
  }

  const handleDelete = (temaId: string) => {
    setTemas(prev => prev.filter(tema => tema.idTema !== temaId))
    onDelete?.(temaId)
  }

  const handleSave = (tema: Tema) => {
    if (editingTema) {
      // Update existing tema
      setTemas(prev => prev.map(t => t.idTema === tema.idTema ? tema : t))
    } else {
      // Add new tema
      setTemas(prev => [...prev, tema])
    }
    setEditingTema(null)
  }

  const handleNewTema = () => {
    setEditingTema(null)
    setIsModalOpen(true)
  }

  const handleCloseModal = () => {
    setIsModalOpen(false)
    setEditingTema(null)
  }

  return (
    <div className="space-y-4">
      <Card>
        <CardHeader>
          <div className="flex items-center justify-between">
            <CardTitle>Temas</CardTitle>
            <Button onClick={handleNewTema} className="flex items-center gap-2">
              <Plus className="h-4 w-4" />
              Novo Tema
            </Button>
          </div>
        </CardHeader>
        <CardContent>
          <div className="flex items-center space-x-2 mb-4">
            <div className="relative flex-1">
              <Search className="absolute left-2 top-2.5 h-4 w-4 text-muted-foreground" />
              <Input
                placeholder="Buscar temas..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="pl-8"
              />
            </div>
          </div>

          <div className="rounded-md border">
            <Table>
              <TableHeader>
                <TableRow>
                  <TableHead>Nome</TableHead>
                  <TableHead>Descrição</TableHead>
                  <TableHead>Áreas Relacionadas</TableHead>
                  <TableHead className="text-center">Prazo</TableHead>
                  <TableHead className="text-center">Tipo Contagem</TableHead>
                  <TableHead className="text-center">Ações</TableHead>
                </TableRow>
              </TableHeader>
              <TableBody>
                {filteredTemas.length === 0 ? (
                  <TableRow>
                    <TableCell colSpan={6} className="text-center py-8 text-muted-foreground">
                      {searchTerm ? 'Nenhum tema encontrado.' : 'Nenhum tema cadastrado.'}
                    </TableCell>
                  </TableRow>
                ) : (
                  filteredTemas.map((tema) => {
                    const areas = getAreasForTema(tema.idAreas)
                    return (
                      <TableRow key={tema.idTema}>
                        <TableCell className="font-medium">{tema.nmTema}</TableCell>
                        <TableCell className="max-w-xs">
                          <div className="truncate" title={tema.dsTema}>
                            {tema.dsTema}
                          </div>
                        </TableCell>
                        <TableCell>
                          <div className="flex flex-wrap gap-1 max-w-sm">
                            {areas.length === 0 ? (
                              <span className="text-muted-foreground text-sm">Nenhuma área</span>
                            ) : (
                              areas.map((area) => (
                                <Badge key={area.idArea} variant="outline" className="text-xs">
                                  {area.nmArea}
                                </Badge>
                              ))
                            )}
                          </div>
                        </TableCell>
                        <TableCell className="text-center">
                          {tema.nrDiasPrazo} dias
                        </TableCell>
                        <TableCell className="text-center">
                          <Badge variant={tema.tpContagem === 'UTEIS' ? 'default' : 'secondary'}>
                            {tema.tpContagem === 'UTEIS' ? 'Úteis' : 'Corridos'}
                          </Badge>
                        </TableCell>
                        <TableCell className="text-center">
                          <DropdownMenu>
                            <DropdownMenuTrigger asChild>
                              <Button variant="ghost" className="h-8 w-8 p-0">
                                <MoreHorizontal className="h-4 w-4" />
                                <span className="sr-only">Abrir menu</span>
                              </Button>
                            </DropdownMenuTrigger>
                            <DropdownMenuContent align="end">
                              <DropdownMenuItem onClick={() => handleEdit(tema)}>
                                <Edit className="mr-2 h-4 w-4" />
                                Editar
                              </DropdownMenuItem>
                              <DropdownMenuItem 
                                onClick={() => handleDelete(tema.idTema)}
                                className="text-destructive"
                              >
                                <Trash2 className="mr-2 h-4 w-4" />
                                Excluir
                              </DropdownMenuItem>
                            </DropdownMenuContent>
                          </DropdownMenu>
                        </TableCell>
                      </TableRow>
                    )
                  })
                )}
              </TableBody>
            </Table>
          </div>
        </CardContent>
      </Card>

      <TemaModal
        isOpen={isModalOpen}
        onClose={handleCloseModal}
        onSave={handleSave}
        tema={editingTema}
      />
    </div>
  )
}
